import {useState} from "react";

const initialItems = [
  {id:1,description: "Passport",quantity: 2, packed: false},
  {id:2,description: "Socks",quantity: 1, packed: false},
  {id:3,description: "Charger",quantity: 1, packed: true},

];


export default function App(){
  return <div className="app">
    <Logo />
    <Form />
    <PackingList />
    <Stat />
  </div>
}


function Logo(){
  return <h1>üå¥Far Awayüå¥</h1>
}

function Form() {
  const [description, setDescription] = useState("");
  const [quantity,setQuantity] = useState(1);
  const [items,setItems] = useState([]);


  function handelAddItems(item){
    setItems(items=> [...items] )
  }


  function handleSubmit(e) {
    e.preventDefault();

    if(!description) return;

    const newItem = {description,quantity,packed: false,id:Date.now};
    console.log(newItem);

    handelAddItems(newItem);

    setDescription("");
    setQuantity(1);

  }

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your trip?</h3>

      <select 
      value={quantity} 
      onChange={(e) => setQuantity(Number(e.target.value))}>
        {Array.from({ length: 20 }, (_, i) => i + 1).map(num => (
          <option value={num} key={num}>
            {num}
          </option>
        ))}
      </select>

      <input 
      type="text" 
      placeholder="Item...."  
      value={description} 
      onChange={(e) => setDescription(e.target.value)}
      />

      <button>Add</button>

    </form>
  );
}

function PackingList(){
    return(
    <div className="list">
      <ul>
        {initialItems.map((item)=>(
        <Item item={item} key={item.id}
        />
        ))}
      </ul>
    </div>
    );
  }


function Item({item}){
  return <li>
    <span style={item.packed ? {textDecoration: "line-trough"} 
      : {}}>
      {item.quantity} {item.description}
      </span>
      <button>‚ùå</button>
  </li>
}

function Stat(){
    return <footer className="stats">
      You have X number on your list and you already packed X (X%)
    </footer>
}
